sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...
  var i = parseInt(inputs.p_pro_value);

  var gd = new GlideDate();
  gd.setValue(inputs.p_info_stopdat);
  var gd_af = gd.getByFormat("yyyy/MM/dd")

  var d = (new Date(gd_af)).getTime();

  var beforMilliseconds = d - 1000 * 3600 * 24 * i;
  //gs.info("beforMilliseconds = " + beforMilliseconds + inputs.p_pro_number);

  var beforday = new Date();
  beforday.setTime(beforMilliseconds);

  var strYear = beforday.getFullYear();
  var strDay = beforday.getDate();
  var strMonth = beforday.getMonth() + 1;
  if (strMonth < 10) {
    strMonth = "0" + strMonth;
  }

  var strBeforday = strYear + "-" + strMonth + "-" + strDay;
  //var newDateBefore = /\d{4}-\d{1,2}-\d{1,2}/g.exec(strBeforday);

  //gs.info("strBeforday = " + strBeforday + inputs.p_pro_number);
  //gs.info("newDateBefore = " + newDateBefore + inputs.p_pro_number);

  outputs.p_newdatebefore = strBeforday;
  var p_today_str = new GlideDate(); 
  p_today_str.setValue(p_today_str);
  outputs.p_today = p_today_str.getValue();
  gs.info("outputs.p_newdatebefore = " + outputs.p_newdatebefore + inputs.p_pro_number);
  gs.info("outputs.p_today = " + outputs.p_today + inputs.p_pro_number);
})(inputs, outputs);

