sys_variable_value.value:
(function execute(inputs, outputs) {
  // ... code ...
  function issbccase(strTmp) {
    //gs.info("strTmp.length = " + strTmp.length);
    for (var i = 0; i < 40; i++) {
      //gs.info("strTmp.charCodeAt(i) = " + strTmp.charCodeAt(i));
      if (strTmp.charCodeAt(i) > 128){
          return true; 
          break; 
      }
    }
    return false;
  }

  var rec = new GlideRecord("x_58872_needit_uim_riyousya_upload_error_info");
  rec.addQuery("sys_id", inputs.p_2.sys_id);
  rec.query();

  while(rec.next()){
    if(!issbccase(rec.common_id)){
      //gs.info("全角なし");
    } else {
      //gs.info("全角あり");
      rec.error_information = rec.common_id + "全角あり";
    }
    rec.upload_id = inputs.p_1;
    rec.update();
  }

  outputs.variable = rec;

  return rec;
})(inputs, outputs);

