sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {
    //gs.info("current.sys_id = " + current.sys_id);
    var rec = new GlideRecord('x_58872_needit_riyosha');
	rec.addQuery('t3_sys_id', current.sys_id);
    rec.query();
    while (rec.next()) {
		//gs.info(rec.getValue('t3_sys_id') + ' t3 exists');
        //gs.info(rec.getValue('t3_notice_address') + ' t3 exists');
		//gs.info(rec.getValue('t4_sys_id') + ' t4 exists');
		//gs.info(rec.getValue('t4_notice_address') + ' t4 exists');

		var a = rec.getValue('t3_last_kanji_name_short') + rec.getValue('t3_first_kanji_name_short') + ' ' + rec.getValue('t3_notice_address');
		//gs.info("a = " + a);
		current.setValue('notice_address', a);
		gs.info("notice_address = " + current.getValue('notice_address'));
		email.addAddress("cc", rec.getValue('t4_notice_address') + '', rec.getValue('t4_last_kanji_name_short') + ' ' + rec.getValue('t4_first_kanji_name_short'));
    }
})(current, template, email, email_action, event);
