sys_script_include.script:
var SendUserEmail = Class.create();

// Extend the global.AbstractAjaxProcessor class
SendUserEmail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    // Define the getEmail function.  
    // Create a GlideRecord for the User table.
    // Use the sysparm_userID passed from the client side to retrieve a record from the User table.
    // Return the email address for the requested record
    getEmail: function() {
        var userRecord = new GlideRecord("x_58872_needit_user_role");
        //         userRecord.get(this.getParameter('sysparm_userName'), this.getParameter('sysparm_userName'));
        //         gs.info("1111 = " + userRecord.number);
        //         gs.info("2222 = " + this.getParameter('sysparm_userName'));
        //         gs.info("3333 = " + userRecord);
        userRecord.addQuery('user_name', '=', this.getParameter('sysparm_userName'));
        userRecord.query();

        while (userRecord.next()) {
            //gs.info("userRecord.number = " + userRecord.number);
            //gs.info("userRecord.user_name = " + userRecord.user_name);
            //gs.info("userRecord.group = " + userRecord.group);
            var userGroup = userRecord.group;
        }

        var groupQuerryTasks = new GlideRecord('x_58872_needit_user_role');
        groupQuerryTasks.addQuery('group', '=', userGroup);
        groupQuerryTasks.query();

        while (groupQuerryTasks.next()) {
            //gs.info("User Role Group = " + groupQuerryTasks.group);
            gs.eventQueue('x_58872_needit.sendEmailTasks', groupQuerryTasks, groupQuerryTasks.number, groupQuerryTasks.email);
        }

        //gs.info("END return= " + userRecord.number);
        return;
    },
    type: 'SendUserEmail'
});
